import React, { useEffect, useState } from "react";
import { Switch, Route } from "react-router-dom";
import SignUp from "./SignUp";
import Login from "./Login";
import NavBar from "./NavBar";
import Home from "./Home";
import Cart from "./Cart";
import "../App.css";
import Supplies from "./Supplies";
import Bonsais from "./Bonsais";
import UpdateUser from "./UpdateUser";
import Items from "./Items";

function App() {
  const [user, setUser] = useState(null);
  const [itemType, setItemType] = useState("");

  console.log(itemType);

  useEffect(() => {
    fetch("/me").then((r) => {
      if (r.ok) {
        r.json().then((user) => setUser(user));
      }
    });
  }, []);

  return (
    <>
      <NavBar user={user} setUser={setUser} setItemType={setItemType} />
      <Switch>
        <Route path="/signup">
          <SignUp setUser={setUser} />
        </Route>
        <Route path="/login">
          <Login setUser={setUser} />
        </Route>
        <Route path="/cart">
          <Cart user={user} setUser={setUser} />
        </Route>
        <Route path="/supplies">
          <Supplies user={user} />
        </Route>
        <Route path="/bonsais">
          <Bonsais user={user} itemType={itemType} />
        </Route>
        <Route path="/cart">
          <Cart user={user} />
        </Route>
        <Route path="/cart-floating">
          <Cart user={user} />
        </Route>
        <Route path="/update-User">
          <UpdateUser user={user} setUser={setUser} />
        </Route>
        <Route path="/items">
          <Items itemType={itemType} user={user} />
        </Route>
        <Route path="/">
          <Home />
        </Route>
      </Switch>
    </>
  );
}

export default App;
