import { useState } from "react";

function UpdateUser() {
  const [updateName, setUpdateName] = useState("");
  const [updatePass, setUpdatePass] = useState("");
  const [updatePassConf, setUpdatePassConf] = useState("");
  const [updateImg, setUpdateImg] = useState("");

  function updateUsername() {
    fetch("/update", {
      method: "PUT",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify({
        username: `${updateName}`,
      }),
    });
  }

  function updatePassword() {
    fetch("/update", {
      method: "PUT",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify({
        password: `${updatePass}`,
        password_confirmation: `${updatePassConf}`,
      }),
    });
  }

  function updateImgURL() {
    fetch("/update", {
      method: "PUT",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify({
        img_url: `${updateImg}`,
      }),
    });
  }

  return (
    <div>
      <button onClick={setUpdateParam("username")}>Update Username</button>
      <div>
        <form name="username" onSubmit={updateUsername}>
          <input onChange={(e) => setUpdateName(e.target.value)}></input>
          <button type="submit">Update Username</button>
        </form>
      </div>
      <div>
        <form name="username" onSubmit={updatePassword}>
          <label>New Password</label>
          <input onChange={(e) => setUpdatePass(e.target.value)}></input>

          <label>Confirm New Password</label>
          <input onChange={(e) => setUpdatePassConf(e.target.value)}></input>
          <button type="submit">Update password</button>
        </form>
      </div>
      <div>
        <form name="username" onSubmit={updateImgURL}>
          <input onChange={(e) => setUpdateImg(e.target.value)}></input>
          <button>Update Profile Pic</button>
        </form>
      </div>
    </div>
  );
}
export default UpdateUser;
