// import { Toast } from "bootstrap";
import { useState } from "react";
import { useHistory } from "react-router-dom";
import Login from "./Login";
import SignUp from "./SignUp";
import { toast, ToastContainer } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

function Cart({ user, setUser }) {
  const [usersItems, setUsersItems] = useState([]);
  const [total, setTotal] = useState(0);
  const history = useHistory();

  useState(() => {
    fetch("/cart")
      .then((r) => r.json())
      .then((cart) => setUsersItems(cart));
  }, []);
  // useState(() => {
  //   fetch("/cart")
  //     .then((r) => r.json())
  //     .then((cart) => console.log(cart));
  // }, []);

  function handleDelete(e) {
    e.preventDefault();

    // fetch(`/remove/${e.target.value}`, { method: "DELETE" });
    console.log(e.target.value);
    console.log(e.target.name);
    // const notify = () =>
    toast.success(
      <div>
        <h1>
          Are you sure you'd like to remove {e.target.name} from your cart?
        </h1>
        <button
          // onClick={console.log(e.target.value)}
          onClick={(e) =>
            fetch(`/remove/${e.target.value}`, { method: "DELETE" })
          }
        >
          Yes
        </button>
        <button>No</button>
      </div>
    );
    // history.go(0);
    // dialogue to go to cart
  }

  // console.log(user?.users_items.length);

  const listUserItems = usersItems?.map((usersItem) => (
    <div className="product" key={usersItem.id}>
      <img src={usersItem.item.img_url} alt="product"></img>
      <h1>{usersItem.item.name}</h1>
      <h1>${usersItem.item.price}</h1>
      <button
        className="add-cart"
        value={usersItem.id}
        name={usersItem.item.name}
        onClick={handleDelete}
      >
        Remove
      </button>
      <ToastContainer />
    </div>
  ));

  // console.log(user);

  useState(() => {
    fetch("/total")
      .then((r) => r.json())
      .then((total) => setTotal(total));
  }, []);

  return (
    <div>
      {user ? (
        <div>
          <div className="shop">{listUserItems}</div>
          <h3 className="total">Total: ${total}</h3>
        </div>
      ) : (
        <div className="log-sign">
          <h3>Please</h3>
          <Login setUser={setUser}></Login>
          <h3>or</h3>
          <SignUp></SignUp>
          <h3>to view cart</h3>
        </div>
      )}
    </div>
  );
}
export default Cart;
