import Button from "@material-ui/core/Button";
import { useState } from "react";
import { useHistory } from "react-router-dom";
import { Link } from "react-router-dom";

function Menu() {
  const history = useHistory();
  const [itemType, setItemType] = useState("");

  function handleItemSelect(type) {
    // e.preventDefault();
    setItemType(type);
  }

  console.log(itemType);

  return (
    <div className="menu">
      <div className="m-left">
        <>
          <Button
            variant="outlined"
            color="primary"
            onClick={(e) => history.push("/")}
          >
            Home
          </Button>
          <Link
            to="/items"
            onClick={e + setItemType("Plant")}
            itemType={itemType}
          >
            Item
          </Link>
          <Button
            variant="outlined"
            color="primary"
            onClick={(e) => handleItemSelect("Plant")}
          >
            Bonsais
          </Button>
          <Button
            variant="outlined"
            color="primary"
            onClick={(e) => handleItemSelect("Accessory")}
          >
            Supplies
          </Button>
          <Button
            variant="outlined"
            color="primary"
            onClick={(e) => history.push("/cart")}
          >
            Cart
          </Button>
        </>
      </div>
    </div>
  );
}

export default Menu;
