import {
  Button,
  Dialog,
  DialogActions,
  DialogContent,
} from "@material-ui/core";
import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";

function Items({ itemType }) {
  const [open, setOpen] = React.useState(false);
  const [items, setItems] = useState([]);
  const history = useHistory();

  const handleClickToOpen = (e) => {
    setOpen(true);
    setItemName(e.target.name);
  };

  const handleToClose = () => {
    setOpen(false);
  };

  useEffect(() => {
    fetch(`/items/${itemType}`)
      .then((r) => r.json())
      .then((item) => setItems(item));
  }, [itemType]);

  function handleSubmit(e) {
    e.preventDefault();
    fetch("/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        item_id: e.target.value,
      }),
    });
    handleClickToOpen(e);
  }

  //   function handleItemSelect(type) {
  //     // e.preventDefault();
  //     setItemType(type);
  //   }

  console.log(itemType);

  const handleNavigateCart = (
    <Dialog open={open}>
      <DialogContent>{itemName} added to your cart</DialogContent>
      <DialogActions>
        <Button onClick={(e) => history.push("/cart")}>Go to Cart</Button>
        <Button onClick={handleToClose}>Continue Shopping</Button>
      </DialogActions>
    </Dialog>
  );

  const listPlants = items?.map((plant) => (
    <div className="product" key={plant.id}>
      <img src={plant.img_url} alt="product"></img>
      <h2>{plant.name}</h2>
      <h2>${plant.price}</h2>
      <button
        className="add-cart"
        value={plant.id}
        name={plant.name}
        onClick={(e) => handleSubmit(e)}
      >
        Add to Cart
      </button>
    </div>
  ));

  return (
    <div className="shop">
      {listPlants} {handleNavigateCart}
      {/* <button onClick={(e) => handleItemSelect("Accessory")}>Accessory</button>
      <button onClick={(e) => handleItemSelect("Plant")}>Plant</button> */}
    </div>
  );
}
export default Items;
