import { Link } from "react-router-dom";
import Menu from "./Menu";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Login from "./Login";
import Logout from "./Logout";
import SignUp from "./SignUp";
import { useState } from "react";

function NavBar({ user, setUser }) {
  const notify = () => toast(`Hello ${user.username}`);
  const [updateParams, setUpdateParams] = useState("");

  function updateUser() {
    fetch("/update", {
      method: "PUT",
      headers: {
        "Content-type": "application/json",
      },
      body: JSON.stringify({
        username: { updateParams },
      }),
    });
  }

  function updateParamsfunc(e) {
    e.preventDefault();
    console.log(e.target.name && updateParams);
  }

  return (
    <>
      <header className="header">
        <div className="h-mid">
          <Link to="/">
            <img
              src="https://riverbendgardens.com/wp-content/uploads/2020/01/logo.png"
              alt="Logo"
            />
          </Link>
        </div>
        <div className="h-right">
          <div>
            {user ? (
              <div className="user">
                <img
                  className="profile-pic"
                  src={user.img_url}
                  alt="profile"
                  onClick={notify}
                ></img>
                <p>Welcome, {user.username}!</p>
                <Logout setUser={setUser} />
                <div>
                  <form name="username:" onSubmit={updateParamsfunc}>
                    <input
                      onChange={(e) => setUpdateParams(e.target.value)}
                    ></input>
                    <button>Update username</button>
                  </form>
                </div>
              </div>
            ) : (
              <div className="log-sign">
                <Login setUser={setUser}></Login>
                <SignUp setUser={setUser}></SignUp>
              </div>
            )}
          </div>
        </div>
      </header>

      <Menu />
    </>
  );
}

export default NavBar;
