import { useState } from "react";
import { useHistory } from "react-router-dom";
import Login from "./Login";
import SignUp from "./SignUp";

function Cart({ user }) {
  const [cartItems, setCartItems] = useState([]);
  const [total, setTotal] = useState(0);
  const history = useHistory();

  useState(() => {
    fetch("/cart")
      .then((r) => r.json())
      .then((cart) => setCartItems(cart));
  }, [cartItems.length]);

  function handleDelete(e) {
    e.preventDefault();
    fetch(`/remove/${e.target.value}`, { method: "DELETE" });
    // history.go(0);
  }

  const listCartItems = cartItems?.map((item) => (
    <div className="product" key={item.id}>
      <img src={item.img_url} alt="product"></img>
      <h1>{item.name}</h1>
      <h1>${item.price}</h1>
      <button className="add-cart" value={item.id} onClick={handleDelete}>
        Remove
      </button>
    </div>
  ));

  useState(() => {
    fetch("/total")
      .then((r) => r.json())
      .then((total) => setTotal(total));
  }, [cartItems.length]);

  return (
    <div>
      {user ? (
        <div>
          <div className="shop">{listCartItems}</div>
          <h3 className="total">Total: ${total}</h3>
        </div>
      ) : (
        <div className="log-sign">
          <h3>Please</h3>
          <Login></Login>
          <h3>or</h3>
          <SignUp></SignUp>
          <h3>to view cart</h3>
        </div>
      )}
    </div>
  );
}
export default Cart;
