import { useState } from "react";
import { Link } from "react-router-dom";

function Cart({ user }) {
  const [usersItems, setUsersItems] = useState([]);
  const [total, setTotal] = useState(0);

  useState(() => {
    fetch("/cart")
      .then((r) => r.json())
      .then((cart) => setUsersItems(cart));
  }, [usersItems.length]);

  function handleDelete(e) {
    e.preventDefault();
    fetch(`/remove/${e.target.value}`, { method: "DELETE" });
  }

  const listUserItems = usersItems?.map((usersItem) => (
    <div className="cart-items" key={usersItem.id}>
      <div className="cart-img-div">
        <img
          className="cart-img"
          src={usersItem.item.img_url}
          alt="profile-pic"
        ></img>
      </div>
      <h1>{usersItem.id}</h1>
      <div className="cart-item">
        <h1>{usersItem.item.name}</h1>
        <h1>${usersItem.item.price}</h1>
      </div>
      <button className="add-cart" value={usersItem.id} onClick={handleDelete}>
        Remove
      </button>
    </div>
  ));

  useState(() => {
    fetch("/total")
      .then((r) => r.json())
      .then((total) => setTotal(total));
  }, [usersItems.length]);

  return (
    <div>
      {user ? (
        <div>
          <h1>{user.username}'s Cart</h1>

          <div>
            {listUserItems}
            <h1>Total: ${total}</h1>
          </div>
        </div>
      ) : (
        <div>
          <h1>
            Please <Link to="/login">Log in</Link> or{" "}
            <Link to="/signup">Sign up</Link> to veiw Cart
          </h1>
        </div>
      )}
    </div>
  );
}
export default Cart;
